import React from 'react';


const Search = (props) => {

    hanlderChang = (prop) => {


        data.sas = prop;

        data.dis = data.dc.filter((number) => {
            return number.name.toLowerCase().indexOf(prop) !== -1;
        });

    };


    let serVal = (e) => {
        let das = e.target.value;
        props.hanchang(das);

    };

    return (
        <form>
            <input onChange={serVal}  type="text" placeholder = "Поиск..." value={props.textarea}/>
            <button type="submit"></button>
        </form>
    )
};


export default Search;
