import React from 'react';


class SwitchMarvel extends React.Component {
    
   
     handlerPas () {
       // alert('qwerty'); 
    this.props.handler()
    }    
        
       render() {
    return (
        <div className='ctalog' >
            {this.props.marvel.map((item) =>
                <div className='catalog1'>
                    <img src={item.image} onClick={this.handlerPas} ></img>

                    <p>{item.name}</p>
                </div>
            )}
        </div>
    )
            }
};


export default SwitchMarvel;
