import React from 'react';


const Search = (props) => {

    let hanlderChang = (prop) => {


        props.state.sas = prop;

        props.state.dis = props.state.marvel.filter((number) => {
            return number.name.toLowerCase().indexOf(prop) !== -1;
        });

    };
deb

    let serVal = (e) => {
        let das = e.target.value;
        hanlderChang(das);

    };

    return (
        <form>
            <input onChange={serVal}  type="text" placeholder = "Поиск..." value={props.state.sas}/>
            <button type="submit"></button>
        </form>
    )
};


export default Search;
